package com.hlj.java8.Stream;import org.junit.Test;import java.util.ArrayList;import java.util.List;/** * @Desc: skip跳过执行结果的前几个，返回值为Stream * @Author HealerJean * @Date 2018/9/14  上午11:48. */public class Demo05Skip {    @Test    public void demo(){        List<String> list = new ArrayList<>();        list.add("赵丽颖");        list.add("张宇晋");        list.add("赵春宇");        list.add("林铁");        list.add("杜闯");        list.add("高彤");        list.add("张翠翠");        list.add("张三丰");        list.stream().filter(s->s.length()==2).skip(1).forEach(s->{            System.out.println(s);        });        /**         杜闯         高彤         */    }}